import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import App from './App';
import Popup from "./Popup";

import styles from './index.module.css';
import validator from 'validator';
import Select from "react-select";

class MyForm extends React.Component {
    constructor(props) {
        super(props);
        this.setState = {
            username: '',
            email: null,
            gender: '',
            errormessageName: '*This field is required',
            errormessageEmail: '*This field is required',
            errormessageGender: '*This field is required',
            disabled: false,
            isOpen: false,
            selection: 'gender'
        };
        this.baseState=this.state;
    }
    mySubmitHandler = (event) => {
        event.preventDefault();
        if(this.state.errormessageName === ''  && this.state.errormessageEmail === '' && this.state.errormessageGender === ''){
            this.setState({disabled: true});
            let url = 'https://api.sheety.co/a059a44709599c766a704967b9cc3d24/subscribers/subscribers';
            let body = {
                subscriber: {
                    fullName: this.state.username,
                    gender: this.state.gender,
                    emailAddress: this.state.email,
                    state: "subscribed"
                }
            }
            fetch(url, {
                method: "POST",
                body: JSON.stringify(body),
                headers: {"Content-type": "application/json; charset=UTF-8"}
            })
                .then((response) => response.json())
                .then(json => {
                    this.setState({isOpen: true})
                    // Do something with object
                    console.log(json.subscriber);
                });
        }
    }
    myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        let errN = '';
        let errE = '';

        if (nam === "email") {
            if (!validator.isEmail(val)) {
                errE = <strong>This email is not valid</strong>;
                this.setState({errormessageEmail: errE});
            }
            else
                this.setState({errormessageEmail: ''});
        }
        else if (nam === 'username'){
            let letters = /^[A-Za-z]+$/;
            if (!val.match(letters)) {
                errN = <strong>Please use only letters </strong>
                this.setState({errormessageName: errN});
            }
            else
                this.setState({errormessageName: ''});
        }
        else if (nam === 'gender'){
            if(val !== ''){
                this.setState({errormessageGender: ''});
            }
        }
        this.setState({[nam]: val});
    }

    closeWindow = (event) => {
        event.preventDefault();

        const{selection} = this.state;
        this.setState({selection: 'gender'});
/*
        this.setState({username: ''});
        this.setState({email: null});
        this.setState({gender: ''});
        this.setState({errormessageName: '*This field is required'});
        this.setState({errormessageEmail: '*This field is required'});
        this.setState({errormessageGender: '*This field is required'});
        this.setState({disabled: false});
        this.setState({isOpen: false});

 */

        document.getElementById('uname').value='';
        document.getElementById('email').value='';

        this.setState(this.baseState);
    }
    render() {
        const options = [
            {label: 'Gender', value:'gender'},
            {label: 'Female', value:'female'},
            {label: 'Male', value:'male'},
            {label: 'Diverse', value:'diverse'}
        ];

        return (
            <form onSubmit={this.mySubmitHandler}>
                <h1>ADDITIVE NEWSLETTER</h1>

                <Select
                    className={styles.dropdown} name='gender' disabled={this.state.disabled} onChange={this.myChangeHandler}>
                    options={options}
                    value={this.state.selection}
                </Select><br />
                <p className={styles.err}>
                    {this.state.errormessageGender}
                </p>

                <p>Enter your name:</p>
                <input className={styles.info} id='uname'
                    type='text'
                    name='username'
                    placeholder='My Name'
                    disabled= {this.state.disabled}
                    onChange={this.myChangeHandler}
                /><br />
                <p className={styles.err}>
                    {this.state.errormessageName}
                </p>
                <p>Enter your email:</p>
                <input className={styles.info} id='email'
                    type='text'
                    name='email'
                    placeholder='additive@newsletter.com'
                    disabled= {this.state.disabled}
                    onChange={this.myChangeHandler}
                /><br />
                <p className={styles.err}>
                    {this.state.errormessageEmail}
                </p>
                <input
                    type='submit'
                    disabled={this.state.disabled}
                    />
                <div>
                    {this.state.isOpen && <Popup
                        content={<>
                            <b>YOUR SUBSCRIPTION WAS SUCCESSFUL, {this.state.username.toUpperCase()}!</b>
                            <p>Thanks for your subscription, we are looking forward to send you the greatest news from us!</p>
                        </>}
                        handleClose={this.closeWindow}
                    />}
                </div>
            </form>
        );
    }
}


ReactDOM.render(<MyForm />, document.getElementById('root'));
