import React from 'react';
import ReactDOM from 'react-dom';

import styles from './index.module.css'
import validator from 'validator';

class MyForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            email: null,
            gender: 'Female',
            errormessageName: '*This field is required',
            errormessageEmail: '*This field is required',
            disabled: false
        };
    }
    mySubmitHandler = (event) => {
        event.preventDefault();
        if(this.state.errormessageName === ''  && this.state.errormessageEmail === '') {
            this.setState({disabled: true});
            let url = 'https://api.sheety.co/a059a44709599c766a704967b9cc3d24/subscribers/subscribers';
            let body = {
                subscriber: {
                    fullName: this.state.username,
                    gender: this.state.gender,
                    emailAddress: this.state.email,
                    state: "subscribed"
                }
            }
            fetch(url, {
                method: "POST",
                body: JSON.stringify(body),
                headers: {"Content-type": "application/json; charset=UTF-8"}
            })
                .then((response) => response.json())
                .then(json => {
                    // Do something with object
                    console.log(json.subscriber);
                });
        }
    }
    myChangeHandler = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        let errN = '';
        let errE = '';

        if (nam === "email") {
            if (!validator.isEmail(val)) {
                errE = <strong>This email is not valid</strong>;
                this.setState({errormessageEmail: errE});
            }
            else
                this.setState({errormessageEmail: ''});
        }
        else if (nam === 'username'){
            let letters = /^[A-Za-z]+$/;
            if (!val.match(letters)) {
                errN = <strong>Please use only letters </strong>
                this.setState({errormessageName: errN});
            }
            else
                this.setState({errormessageName: ''});
        }
        this.setState({[nam]: val});
    }
    render() {
        return (
            <form onSubmit={this.mySubmitHandler}>
                <h1>ADDITIVE NEWSLETTER</h1>

                <select className={styles.dropdown} name='gender' disabled={this.state.disabled} onChange={this.myChangeHandler}>
                    <option value="Female">Female</option>
                    <option value="Male">Male</option>
                    <option value="Diverse" >Diverse</option>
                </select>

                <p>Enter your name:</p>
                <input
                    type='text'
                    name='username'
                    disabled= {this.state.disabled}
                    onChange={this.myChangeHandler}
                /><br />
                <p className={styles.err}>
                    {this.state.errormessageName}
                </p>
                <p>Enter your email:</p>
                <input
                    type='text'
                    name='email'
                    disabled= {this.state.disabled}
                    onChange={this.myChangeHandler}
                /><br />
                <p className={styles.err}>
                    {this.state.errormessageEmail}
                </p>
                <input
                    type='submit'
                    disabled={this.state.disabled}
                    />
            </form>
        );
    }
}


ReactDOM.render(<MyForm />, document.getElementById('root'));
