import React, {useState} from 'react';
import ReactDOM from "react-dom";
import './table.css';

class Backend extends React.Component {


    componentDidMount() {
        fetch("https://api.sheety.co/a059a44709599c766a704967b9cc3d24/subscribers/subscribers")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        subscribers: result.subscribers
                    });
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

    render() {
        const { error, isLoaded, subscribers } = this.state;
        if (error) {
            return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
            return <div>Loading...</div>;
        } else {
            return (
                <ul>
                    {subscribers.map(subscriber => (
                        <li key={subscriber.id}>
                            {subscriber.fullName} {subscriber.gender} {subscriber.emailAddress} {subscriber.state}
                        </li>
                    ))}
                </ul>
            );
        }
    }
}
//export default Backend;
export default class Table extends React.Component {
    constructor(props) {
        super(props) //since we are extending class Table so we have to use super in order to override Component class constructor
        this.state = { //state is by default an object

        }
    }

    renderTableData() {
        return subscribers.map((student, index) => {
            const { id, fullName, gender, emailAddress, state } = subscriber //destructuring
            return (
                <tr key={id}>
                    <td>{id}</td>
                    <td>{fullName}</td>
                    <td>{gender}</td>
                    <td>{emailAddress}</td>
                    <td>{state}</td>
                </tr>
            )
        })
    }

    render() {
        return (
            <div>
                <h1 id='title'>React Dynamic Table</h1>
                <table id='students'>
                    <tbody>
                    {this.renderTableData()}
                    </tbody>
                </table>
            </div>
        )
    }
}



