import React, {useState} from 'react';
import './table.css';

export default class Backend extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            items: []
        };
    }

    componentDidMount() {
        fetch("https://api.sheety.co/a4c738ad49ee1d97b368830238289575/subscribers/subscribers")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        subscribers: result.subscribers
                    });
                },
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

    renderTableHeader(){
        let header = Object.keys(this.state.subscribers[0])
        return header.map((key, index) =>{
            return <th key={index}>{key.toUpperCase()}</th>
        })
    }

    handleItemChanged(i, event) {
        let items = this.state.items;

        items[i] = event.target.value;

        this.setState({
            items: items
        });
    }

    updateMessage(event) {
        this.setState({
            message: event.target.value
        });
    }

    render() {
        const { error, isLoaded, subscribers } = this.state;
        if (error) {
            return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
            return <div>Loading...</div>;
        } else {
            return (
                <div>
                    <h1 id='title'>SUBSCRIBERS</h1>
                    <table id='subscribers'>
                        <tbody>
                        <tr>{this.renderTableHeader()}</tr>
                        {subscribers.map(subscriber => (
                            <tr key={subscriber.id} onChange={context.handleItemChanged.bind(context, i)}>
                                <td>{subscriber.id}</td>
                                <td>{subscriber.fullName}</td>
                                <td>{subscriber.gender}</td>
                                <td>{subscriber.emailAddress}</td>
                                <td>{subscriber.state}</td>
                            </tr>
                        ))}
                        </tbody>
                    </table>

                </div>
            );
        }
    }
}
